class Solution {
    public int numberOfAlternatingGroups(int[] c, int k) {
        int n = c.length;
        int m = 2 * n;
        int[] t = new int[m];
        for(int i = 0; i < n; i++) {
            t[i] = c[i];
            t[i + n] = c[i];
        }
        int ans = 0;
        for(int i =0; i < n; ) {
            int j = i + 1;
            while(j < 2 * n && t[j] != t[j - 1])j++;
            int x = Math.min(j - k, n - 1);
            // System.out.println(i+" " +j+" "+x);
            if(x >= i)ans += (x - i + 1);
            i= j;
        }
        return ans;
    }
}
