class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int countAlternatingGroups = 0;
        int i = 0;
        int j = 1;
        while (j < n + k && i < n) {
            int jVar = j;
            int endVar = jVar % n;
            while (colors[endVar] != colors[(endVar - 1 + n) % n] && jVar < n + k - 1) {
                jVar++;
                endVar = jVar % n;
            }
            int tempVar=jVar-i-k+1;
            countAlternatingGroups += max(tempVar,0);
            i = jVar;
            j = i + 1;
        }
        return countAlternatingGroups;
    }
};
