class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        int ct = 1;
        int lt = arr[0];
        int ans = 0;
        for(int i=1;i<n+k-1;i++){
            int id = i%n;
            if(lt!=arr[id]){
                ct++;
            }
            else{
                ct = 1;
            }
            if(ct>=k){
                ans++;
            }
            lt = arr[id];
        }
        return ans;
    }
};
