class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int i = 0;
        int j = 1;
        int n = colors.size();

        while (j < n + k && i < n) {
            int s = j;
            int e = s % n;

            // Find the end of the current alternating group
            while (colors[e] != colors[(e - 1 + n) % n] && s < n + k - 1) {
                s++;
                e = s % n;
            }

            // Calculate the number of groups found
            ans += max(s - i - k + 1, 0);
            i = s;
            j = i + 1;
        }

        return ans;
    }
};
