class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int r=-1,n=c.size(),ans=0;
        for(int i=0;i<n;i++){
            c.push_back(c[i]);
        }

        for(int i=0;i<n;i++){
            int cnt=0,pre=i;
            while(i+1<2*n && c[i]!=c[i+1]){
                i++;cnt++;
            }
            cnt++;

            if(cnt<k) continue;
            if(i>=n){
                ans += min(cnt-k+1,n-pre);
            } else {
                ans+=cnt-k+1;
            }
        }

        return ans;
    }
};
