class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i,j,n=colors.size();
        vector<int> flag(n,0);
        for(i=0;i<n;i++) {
            if(colors[i] != colors[(i+1)%n] && colors[i] != colors[(i-1+n)%n])
                flag[i] = 1;
        }
        i = 0;
        k -= 2;
        for(j=0;j<k-1;j++)
            flag.push_back(flag[i++]); 
        n = flag.size();
        for(i=1;i<n;i++)
            flag[i] += flag[i-1];
        int ans = 0;
        for(i=k-1;i<n;i++) {
            if(flag[i]-(i-k >= 0 ? flag[i-k] : 0) == k)
                ans++;
        }

        return ans;
    }
};
