class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int l = 0, r = 1;
        int ans = 0;
        while (l < n) {
            while (r-l < k) {
                if (colors[(r-1)%n] != colors[r%n]) {
                    r++;
                } else {
                    l = r;
                    r++;
                    break;
                }
            }
            if (r-l == k) {
                ans++;
                l++;
            }
        }
        return ans;
    }
};
