class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int i=0,j=1;
        int ans=0;
        while(i<n && j<n+k-1){
            if(colors[j]!=colors[j-1]){
                if(j-i+1==k){
                    ans++;
                    i++;
                }
                j++;
            }
            else if(colors[j]==colors[j-1]){
                i=j;
                j++;
            }
        }
        return ans;
    }
};
