class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int ans = 0,i=0,j=1;
    int n = colors.size();

    for(;j<n+k and i<n;) {
        int st = j, d = st % n;
        while (colors[d] != colors[(d - 1 + n) % n] && st < n + k - 1) {
            st++;
            d = st % n;
        }
        ans += max(st - i - k + 1, 0);
        i = st;
        j = i + 1;
    }

    return ans;
    }
};
