class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),ans=0;
        vector<int>arr(3*n);
        for(int i=0;i<3*n;i++)arr[i]=colors[(i%n)];
        int l=-1;

        for(int i=1;i<k;i++){
            if(arr[i]==arr[i-1]){
                l=i-1;

            }
        }
        if(l==-1)ans++;
        for(int i=k;i<n+k-1;i++){
            if(arr[i]!=arr[i-1] && i-l>=k)ans++;
            else if(arr[i]==arr[i-1])l=i-1;
        }
        return ans;
    }
};
