class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        cnt = 0
        n = len(colors)
        for i in range(1,k):
            if colors[i] == colors[i - 1]:
                cnt += 1
        ans = 1 if cnt == 0 else 0
        for i in range(1,n):
            if colors[(i + k - 1) % n] == colors[(i + k - 2) % n]:
                cnt += 1
            if colors[i] == colors[i - 1]:
                cnt -= 1
            if cnt == 0:
                ans += 1
        return ans
