class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n=nums.size();
        int i=0;
        int j=1;
        int ans=0;

    while(j<nums.size()+k-1){
        if(nums[j%n]!=nums[(j-1)%(n)]){
            if((j-i+1)>=k) {
                ans++;
                }
        }
        else{

            i=j;
        }
        j++;
    }

        return ans;
    }
};
