class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k;i++)
        {
            colors.push_back(colors[i]);
        }
        int ans=0;
        int l=0,r=0;
        while(l<colors.size()&&r+1<colors.size())
        {
            if(r-l+1==k)
            {
                ans++;
                l++;
            }

            if(colors[r+1]!=colors[r])
            {
                r++;
            }
            else
            {
                r++;
                l=r;
            }
        }
        return ans;

    }
};
