class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        vector<int>v=c;
        for(int i=0;i<k-1;i++){
            v.push_back(c[i]);
        }
        int ans=0;
        int ct=1;
        int prev=v[0];
        for(int i=1;i<v.size();i++){
            if(v[i]==v[i-1]){
                if(ct-k+1>=0)
                ans+=(ct-k+1);
                ct=0;
            }
            ct++;
            prev=v[i];
        }
        if(ct-k+1>=0)
            ans+=(ct-k+1);
        return ans;

    }
};
