class Solution {
public:
    typedef long long ll;
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        ll n=colors.size();
        ll ans=0;
        vector<bool>dp(n,0);
        for(int i=0;i<colors.size();i++){
            ll x=i;
            ll y=(i+1)%n;
            ll z=(i+2)%n;
            if(colors[y]!=colors[x]&&colors[y]!=colors[z]){
                dp[y]=true;
            }
        }
        for(int i=0;i<n;i++){
            dp.push_back(dp[i]);
        }
        ll aage=2*n;
        for(int i=2*n-1;i>=0;i--){
            if(i<n&&aage>=i+k-1){
                ans++;
            }
            if(dp[i]==false){
                aage=i;
            }
        }

        return ans;
    }
};
