class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int> v;
        int n=c.size();
        int cnt=0;
        bool toggle=c[0];
        for(int i=0;i<n;i++){
            if(c[i]==toggle){
                cnt++;
                toggle=!toggle;
            }else{
                v.push_back(cnt);
                cnt=1;
                toggle=c[i];
                toggle=!toggle;
            }
        }
        v.push_back(cnt);
        if(v.size()>=2){
            if(c[0]!=c[c.size()-1]){
                v[0]+=v[v.size()-1];
                v.pop_back();
            }
        }else{
            if(c[0]==c[n-1]){
                return v[0]-k+1;
            }else{
                if(k<=v[0]){
                    return v[0];
                }
            }
        }
        int ans=0;
        for(int i=0;i<v.size();i++){
            // cout<<v[i]<<" ";
            ans+=max(0,v[i]-k+1);
        }
        return ans;
    }
};
