class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int > c=colors;
        for(auto i:colors)
            c.push_back(i);
        int st=0,ans=0;
        for(int i=1;i<(2*n);i++)
        {
            if(st>=n)
                break;
            if(c[i]==c[i-1])
                st=i;
            if((i-st+1)>k)
                st++;
            if(st>=n)
                break;
            if((i-st+1)==k)
                ans++;


        }
        return ans;
    }
};
