class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0, count = 1;
        for (int i = n - k + 1; i < n; i++) {
            if (colors[i] != colors[i - 1])
                count++;
            else {
                count = 1;
            }
        }
        for (int i = 0; i < n; i++) {
            if (colors[i] != colors[(i - 1 + n) % n]) {
                count++;
                if (count >= k)
                    ans++;
            } else {
                count = 1;
            }
        }
        return ans;
    }
};
