class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(); 
        int cnt=0; 
        int len=1; 
        rep(i,1,n+k-1){ 
            int curr=i%n; 
            int prev=(i-1)%n; 
            if(colors[curr]!=colors[prev]){ 
                len++; 
            }else{ 
                len=1; 
            } 
            if(len>=k) { 
                cnt++; 
            } 
        } 
        return cnt; 
    }
};
