class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int i,j;
        int dp[200001]={0};
        for(i=0;i<n;i++) {
            if(colors[i]!=colors[(i+1)%n]) dp[i]=1;
        }
        for(i=n;i<n+k-2;i++)dp[i]=dp[i%n];
        int ans = 0;
        for(i=1;i<n+k;i++) {
            // cout<<i<<" "<<dp[i]<<" "<<dp[i-1]<<endl;
            if (dp[i]) dp[i] = dp[i] + dp[i-1];
            else {
                if(dp[i-1] >= k-1) ans = ans + (dp[i-1]+2-k);
            }
        }
        return ans;
    }
};
