class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size(), cnt = 0;
        int i = 0, j = 1;
        while (j<n+k && i<n) {
            int ptr = j;
            int rem = ptr%n;
            while(c[rem] != c[(rem-1+n)%n] && ptr<n+k-1){
                ptr++;
                rem = ptr % n;
            }
            cnt += max(ptr-i-k+1, 0);
            i = ptr;
            j = i+1;
        }
        return cnt;
    }
};
