class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i=0,j=1;
        int cnt=0;
        int val=k-1;
        for(int i=0;i<min((int)colors.size(),val);i++){
            colors.push_back(colors[i]);
        }
        int prev=colors[0];
        int ans=0;
        int n=colors.size();
        while(j<n){
            if(colors[j]!=prev){
                cnt++;
            }
            prev=colors[j];
            if((j-i+1)==k){
                if(cnt==k-1){
                    ans++;
           //         cout<<i<<" "<<j<<endl;
                }
                if(colors[i]!=colors[i+1]) cnt--;
                i++; j++;
            }
            else{
                j++;
            }
        }
        return ans;
    }
};
