#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& C, int k) {
        extendArray(C, k);

        int result = countAlternatingGroups(C, k);

        return result;
    }

private:
    void extendArray(vector<int>& C, int k) {
        // Extend array C by adding the first k-1 elements to the end
        C.insert(C.end(), C.begin(), C.begin() + k - 1);
    }

    int countAlternatingGroups(const vector<int>& C, int k) {
        int count = 0;
        int length = C.size();
        int numGroups = 1;

        int i = 1;
        while (i < length) {
            if (C[i] != C[i - 1]) {
                numGroups++;
            } else {
                numGroups = 1;
            }

            if (numGroups >= k) {
                count++;
            }

            i++;
        }

        return count;
    }
};
