class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
       int n = colors.size();
    int count = 0;

    for (int i = 0; i < k - 1; ++i) {
        colors.push_back(colors[i]);
    }

    int alternatingPairs = 0;
    for (int i = 0; i < k - 1; ++i) {
        if (colors[i] != colors[i + 1]) {
            alternatingPairs++;
        }
    }

    if (alternatingPairs == k - 1) {
        count++;
    }

    for (int start = 1; start < n; ++start) {

        if (colors[start - 1] != colors[start]) {
            alternatingPairs--;
        }

        if (colors[start + k - 2] != colors[start + k - 1]) {
            alternatingPairs++;
        }

        if (alternatingPairs == k - 1) {
            count++;
        }
    }

    return count;
    }
};
