    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        for(int i=0;i<k-1;i++) c.push_back(c[i]);
        n=c.size();
        int start=0;
        int ans=0;
        for(int i=1;i<n;i++){
            if(c[i]^c[i-1]==1 ){
                if(i-start+1==k) ans++;
                else if(i-start+1>k){
                    start--;ans++;
                }
            }
            else{
                start=i;
            }
        }
        return ans;
    }
};
