class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
          int n = c.size();
        vector<int> temp(n + k - 1);

        for (int i = 0; i < n; ++i) {
            temp[i] = c[i];
        }
        for (int i = 0; i < k - 1; ++i) {
            temp[n + i] = c[i];
        }

        int result = 0;
        int N = 1;
        for (int i = 1; i < temp.size(); ++i) {
            if (temp[i] != temp[i - 1]) {
                ++N;
            } else {
                N = 1;
            }
            if (N >= k) {
                ++result;
            }
        }

        return result;
    }
};
