class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int N = c.size(); 
        int ans = 0; 
        if (k == 1) { 
            return N; 
        } 
        int len = 1; 
        int i = 1; 
        while (i < N + k - 1) { 
            int curr = i; 
            int prev = i - 1; 
            if (curr >= N) { 
                curr -= N; 
            } 
            if (prev >= N) { 
                prev -= N; 
            } 
            if (c[curr] != c[prev]) { 
                ++len; 
            } else { 
                len = 1; 
            } 
            if (len >= k) { 
                ++ans; 
            } 
            ++i; 
        }
        return ans; 
    }
};
