class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int j = 0;
        int n = colors.size();
        bool c = colors[0];
        for (int i = 0; i < n; i++) {
            if (j <= i) {
                j = i;
                c = colors[i];
            }

            while ((j - i) < k) {
                if (colors[j % n] != c) break;
                j++;
                c = !c;
            }

            if ((j - i) == k) ans++;
        }

        return ans;
    }
};
