import java.util.HashSet;
import java.util.LinkedList;

class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int res = 0;
        int n = colors.length;
        LinkedList<Integer> link = new LinkedList<>();
        int pre = -1;
        HashSet<Integer> first = new HashSet<>();
        for (int i = 0; i < colors.length + k; i++) {
            if (i > k -1 && i % n == k - 1) break;
            int cur = colors[i % n];
            if (link.size() == 0){
                link.addFirst(1);
            }else {
                if (pre == cur){
                    link = new LinkedList<>();
                }else{
                    if (link.size() == k - 1){
                        Integer size = link.removeLast();
                        res += size;
                    }
                }
                link.addFirst(1);
            }
            pre = cur;
        }
        return res;
    }
}
