class Solution {
    public int numberOfAlternatingGroups(int[] c, int k) {
        int ct = 0;
        int pre = 1;
        int n = c.length;
        for(int i = 1;i<k-1;i++){
            if(c[i]!=c[i-1]){
                pre++;
            }else{
                pre = 1;
            }
        }

        for(int i = k-1;i<k-1+n;i++){
            int j = i%n;
            int p = (i-1)%n;
            if(c[j]!=c[p]){
                pre++;
                if(pre>=k) ct++;
            }else{
                pre = 1;
            }
        }
        return ct;
    }
}
