class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
             int len = colors.length; 
            int res = 0; 
            if (k == 1) return len; 
            int alter = 1; 
            int ind = 1; 
            while (ind < len+ k - 1) { 
                int co = ind; 
                int po = ind - 1; 

                if (co >= len)  co -= len; 
                if (po>= len) po -= len; 
                if (colors[co] != colors[po]) ++alter; 
                else alter = 1; 
                if (alter >= k) ++res; 


                ++ind; 
            }
        return res;
}
}
