class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        if (k > n) return 0;
        int cnt = 0, alt = 0;
        for (int i = 0; i < k - 1; i++) {
            if (c[i] != c[i + 1]) {
                alt++;
            }
        }
        for (int i = 0; i < n; i++) {
            int ni = (i + k - 1) % n;
            if (c[ni] != c[(ni + 1) % n]) {
                alt++;
            }
            if (c[i] != c[(i + 1) % n]) {
                alt--;
            }
            if (alt == k - 1) {
                cnt++;
            }
        }
        return cnt;
    }
};
