class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v;
        int n = colors.size();
        // v.insert(v.end(),colors.begin()+n-(k-1),colors.end());
        v.insert(v.end(),colors.begin(),colors.end());
        v.insert(v.end(),colors.begin(),colors.begin()+k-1);
        // for(auto it: v) cout<<it<<" ";
        int i=0;
        int ans=0;
        int j=1;
        while(j<v.size()){
           if(v[j] == v[j-1]) {
            i=j;

           }
           else if(j-i+1==k) {ans++;i++;}
           j++;




        }
        return ans;


    }
};
