class Solution {
public:
int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n= nums.size();
        int cnt = 0;
        if(k==1)return n;
        int ans = 1;
        int Count = 0;
        for(int i=1;i<n+k-1;i++){
            int curr=i;
            int last=i-1;
            if(curr>=n)curr-=n;
            if(last>=n)last-=n;
            if(nums[curr]!=nums[last])ans++;
            else ans = 1;
            if(ans >= k)Count++;
        }
        return Count;
}
};
