/**
 * @param {number[]} colors
 * @param {number} k
 * @return {number}
 */
var numberOfAlternatingGroups = function(colors, k) {
    let n = colors.length;
    let ans = 0;

    // Check initial window of size k
    function isValidWindow(start, end) {
        for (let i = start; i < end - 1; i++) {
            if (colors[i] === colors[i + 1]) {
                return false;
            }
        }
        return true;
    }

    // Initial check for the first window
    let isCurrentWindowValid = isValidWindow(0, k);
    if (isCurrentWindowValid) {
        ans++;
    }

    // Sliding window approach
    for (let i = 1; i < n; i++) {
        if (colors[(i + k - 2) % n] !== colors[(i + k - 1) % n]) {
            if (!isCurrentWindowValid) {
                isCurrentWindowValid = true;
                for (let j = 0; j < k - 1; j++) {
                    if (colors[(i + j) % n] === colors[(i + j + 1) % n]) {
                        isCurrentWindowValid = false;
                        break;
                    }
                }
            }
        } else {
            isCurrentWindowValid = false;
        }
        if (isCurrentWindowValid) {
            ans++;
        }
    }

    return ans;
};
