#define ll long long
class Solution {
public:
    vector<ll> computeLPSArray(vector<ll>& pattern) {
        ll m = pattern.size();
        vector<ll> lps(m, 0);
        ll len = 0;
        ll i = 1;

        while (i < m) {
            if (pattern[i] == pattern[len]) {
                len++;
                lps[i] = len;
                i++;
            } else {
                if (len != 0) {
                    len = lps[len - 1];
                } else {
                    lps[i] = 0;
                    i++;
                }
            }
        }
        return lps;
    }

    vector<ll> KMPSearch(vector<ll>& text, vector<ll>& pattern) {
        ll n = text.size();
        ll m = pattern.size();
        vector<ll> lps = computeLPSArray(pattern);
        vector<ll> occurrences;

        ll i = 0;
        ll j = 0;
        while (i < n) {
            if (pattern[j] == text[i]) {
                i++;
                j++;
            }

            if (j == m) {
                occurrences.push_back(i - j);
                j = lps[j - 1];
            } else if (i < n && pattern[j] != text[i]) {
                if (j != 0) {
                    j = lps[j - 1];
                } else {
                    i++;
                }
            }
        }
        return occurrences;
    }

    vector<ll> createAlternatingPattern(ll k) {
        vector<ll> pattern(k);
        for (ll i = 0; i < k; i++) {
            pattern[i] = i % 2;
        }
        return pattern;
    }


    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        ll n = colors.size();

        vector<ll> extendedColors(colors.begin(), colors.end());
        extendedColors.insert(extendedColors.end(), colors.begin(), colors.end());

        vector<ll> pattern1 = createAlternatingPattern(k);
        vector<ll> pattern2 = pattern1;
        for (ll& val : pattern2) {
            val = 1 - val; 
        }

        vector<ll> occurrences1 = KMPSearch(extendedColors, pattern1);
        vector<ll> occurrences2 = KMPSearch(extendedColors, pattern2);

        ll count = 0;
        for (ll pos : occurrences1) {
            if (pos < n) {
                count++;
            }
        }
        for (ll pos : occurrences2) {
            if (pos < n) {
                count++;
            }
        }

        return count;
    }
};
