class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        vector<int> arr(2*n);
        for (int i=0;i<n;i++) {
            arr[i] = c[i];
        }
        for (int i=0;i<k-1;i++) {
            arr[n+i]=c[i];
        }
        int ans = 0,len = 1;
        for (int i=1;i<n+k-1;i++) {
            if (arr[i]!=arr[i - 1]) {
                len++;
            } 
            else{
                len=1;
            }
            if (len >= k) {
                ans++;
            }
        }
        return ans;
    }
};
