class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int x=n+k-1;
        vector<int> v(x);

        for (int i = 0; i < n; i++) {
            v[i] = colors[i];
        }
        for (int i = 0; i < k - 1; i++) {
            v[n + i] = colors[i];
        }

        int cnt = 0;
        int val = 1;
        int m=v.size();
        for (int i = 1; i < m; i++) {
            if (v[i] != v[i - 1]) {
                val++;
            } else {
                val = 1;
            }
            if (val >= k) {
                cnt++;
            }
        }
        return cnt;
    }
};
