class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;

        for(int i = 0; i < k - 1; i++) {
            colors.push_back(colors[i]);
        }

        int count = 0, j = 0, prev = (colors[0] == 0 ? 1 : 0), n = colors.size();
        for(int i = 0; i < n; i++) {
            if(prev == colors[i]) {
                count = 1;
            }
            else count++;
            prev = colors[i];
            if(count >= k) {
                ans++;
            }
        }

        return ans;
    }
};
