class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        for (int i=0; i<k-1; ++i) {
            c.push_back(c[i]);
        }
        int i = 0, j = 1;
        int adj = 0;
        int ans = 0;
        while (j < k-1) {
            if (c[j] == c[j-1]) {
                adj++;
            }
            j++;
        }
        while (j < c.size()) {
            if (c[j] == c[j-1]) {
                adj++;
            }
            j++;
            if (adj == 0) {
                ans++;
            }
            if (c[i] == c[i+1]) {
                adj--;
            }
            i++;
        }
        return ans;
    }
};
