class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int ans = 0;

        if(colors.size() < k) return 0;

        for(int i=0; i<k-1; i++)
        {
            colors.push_back(colors[i]);
        }

        int cnt = 0;

        for(int i=0; i<colors.size()-1; i++)
        {
            if(colors[i] != colors[i+1]) cnt++;
            else cnt = 0;

            if(cnt == k-1){
                ans++;
                cnt = k-2;
            }
            cout<<colors[i]<<" ";
        }

        return ans;
    }
};
