class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n = colors.size();
        int dp[n][2];

        dp[0][0] = (colors[0]==0);
        dp[0][1] = (colors[0]==1);

        for(int i=1;i<n;i++){
            if(colors[i]==0){
                dp[i][0] = 1 + dp[i-1][1];
                dp[i][1] = 0;
            }else{
                dp[i][1] = 1 + dp[i-1][0];
                dp[i][0] = 0;
            }
        }
        // for(int i=0;i<n;i++){
        //     cout<<dp[i][0]<<" "<<dp[i][1]<<"\n";
        // }
        int ans=0;

        for(int i=0;i<n;i++){
            if(dp[i][0] >= k || dp[i][1] >= k){
                ans++;
            }
        }

        for(int i=0;i<k-1;i++){
            int left = k-i-1;
            if(i%2){
                if(dp[i][0] >= i+1 && dp[n-1][0] >= left){
                    ans++;
                }else if(dp[i][1] >= i+1 && dp[n-1][1] >= left){
                    ans++;
                }
            }else{
                if(dp[i][0] >= i+1 && dp[n-1][1] >= left){
                    ans++;
                }else if(dp[i][1] >= i+1 && dp[n-1][0] >= left){
                    ans++;
                }
            }
        }
        return ans;
    }
};
