class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(), colors.begin(), colors.begin() + k - 1);

        int count = 0;
        int current_length = 1;  // To track the length of the current alternating sequence
        int total_length = colors.size();

        // Traverse through the extended array
        for (int i = 1; i < total_length; ++i) {
            if (colors[i] != colors[i - 1]) {
                ++current_length;
            } else {
                current_length = 1;
            }

            // Check if we have a valid alternating sequence of length k
            if (current_length >= k) {
                ++count;
            }
        }

        return count;
    }
};
