class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        const int n = colors.size();

        for (int i = 0; i < n; ++i)
            colors.emplace_back(colors[i]);

        int res = 0;

        for (int i = 0, start = 0; i < 2 * n; ++i) {
            if (i == 0 || colors[i] != colors[i - 1])
                ;  // no need to move start
            else
                start = i;

            if (i >= n) {
                if (i - start + 1 >= k) ++res;
            }
        }

        return res;
    }
};
