class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int ans = 0;
        int n = c.size();
        int l = 0;
        for (int r = 1; r < n + k - 1; r++) {
            if (c[r % n] == c[(r - 1) % n]) {
                l = r;
            } else {
                if (r - l + 1 >= k) {
                    ans++;
                }
            }
        }
        return ans;
    }
};
