class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& color, int k) {
        int n = color.size();
        vector<int>newColor(n+k-1);
        for(int i = 0; i<n; i++){
            newColor[i] = color[i];
        }
        for(int i = 0; i<=k-2; i++){
            newColor[n+i] = color[i];
        }
        int res = 0;
        int curr = 1;
        for(int i = 1; i<newColor.size(); i++){
            if(newColor[i-1] != newColor[i]){
                curr += 1;
            }
            else{
                curr = 1;
            }
            if(k <= curr){
                res++;
            }
        }
        return res;
    }
};
