class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v;
        v=colors;
        int ans=0;
        for(int i=0;i<k-1;i++){
            v.push_back(colors[i]);
        }
        int n=v.size();
        int check=1;
        for(int i=0;i<n-1;i++){
            if(v[i]!=v[i+1]){
                check++;
                if(check==k){
                    ans++;i++;
                    while(i<n-1 && v[i]!=v[i+1]){
                        ans++;
                        i++;
                    }
                check=1;
                }
            }
            else check=1;
        }
        return ans;
    }
};
