class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr1, int k) {

        int n = arr1.size(),ans = 0;
        vector<int> prr(n + k - 1);

        for (int i = 0; i < n; ++i) {
            prr[i] = arr1[i];
        }
        for (int i = 0; i < k - 1; ++i) {
            prr[n + i] = arr1[i];
        }


        int k1 = 1;
        for (int i = 1; i < prr.size(); ++i) {
            if (prr[i] != prr[i - 1]) {
                ++k1;
            } else {
                k1 = 1;
            }
            if (k1 >= k) {
                ans++;
            }
        }

        return ans;


    }
};
