class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        s = 0
        prev = -1
        res = 0
        for i in range(len(colors)+k-1):
            cur = colors[i%len(colors)]
            if cur != prev:
                s += 1
            else:
                s = 1
            if s >= k:
                res += 1
            prev = cur
        return res
