class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        if (k > n) return 0;
        int[] diffs = new int[n];
        for (int i = 0; i < n; i++) {
            diffs[i] = (colors[i] != colors[(i + 1) % n]) ? 1 : 0;
        }
        int ans = 0;
        int currentCount = 0;
        for (int i = 0; i < k - 1; i++) {
            currentCount += diffs[i];
        }

        for (int i = 0; i < n; i++) {
            if (currentCount == k - 1) {
                ans++;
            }
            currentCount -= diffs[i];
            currentCount += diffs[(i + k - 1) % n];
        }

        return ans;
    }
}
